# =============================================================================
# SOURCE: guides/tiered-prefix-cache/cpu/manifests/inferencepool/values.yaml
# Lines 16-40:
#   pluginsCustomConfig:
#     custom-plugins.yaml: |
#       apiVersion: inference.networking.x-k8s.io/v1alpha1
#       kind: EndpointPickerConfig
#       plugins:
#       - type: queue-scorer
#       - type: kv-cache-utilization-scorer
#       - type: prefix-cache-scorer
#         name: gpu-prefix-cache-scorer
#       - type: prefix-cache-scorer
#         name: cpu-prefix-cache-scorer
#         parameters:
#           autoTune: false
#           lruCapacityPerServer: 41000
#       schedulingProfiles:
#       - name: default
#         plugins:
#         - pluginRef: queue-scorer
#           weight: 2.0
#         - pluginRef: kv-cache-utilization-scorer
#           weight: 2.0
#         - pluginRef: gpu-prefix-cache-scorer
#           weight: 1.0
#         - pluginRef: cpu-prefix-cache-scorer
#           weight: 1.0
#
# NOTE: This configuration uses two prefix-cache-scorer plugins:
# - gpu-prefix-cache-scorer: Tracks GPU cache hits
# - cpu-prefix-cache-scorer: Tracks CPU cache hits (LMCache offloading)
# The lruCapacityPerServer: 41000 allocates ~100GB for Qwen-32B on CPU.
# autoTune is disabled because vLLM doesn't emit CPU capacity metrics yet.
# =============================================================================
export LLMDBENCH_VLLM_MODELSERVICE_GAIE_CUSTOM_PLUGINS=$(mktemp)
cat << EOF > $LLMDBENCH_VLLM_MODELSERVICE_GAIE_CUSTOM_PLUGINS
custom-plugins.yaml: |
  apiVersion: inference.networking.x-k8s.io/v1alpha1
  kind: EndpointPickerConfig
  plugins:
  - type: queue-scorer
  - type: kv-cache-utilization-scorer
  - type: prefix-cache-scorer
    name: gpu-prefix-cache-scorer
  - type: prefix-cache-scorer
    name: cpu-prefix-cache-scorer
    parameters:
      autoTune: false
      lruCapacityPerServer: 41000
  schedulingProfiles:
  - name: default
    plugins:
    - pluginRef: queue-scorer
      weight: 2.0
    - pluginRef: kv-cache-utilization-scorer
      weight: 2.0
    - pluginRef: gpu-prefix-cache-scorer
      weight: 1.0
    - pluginRef: cpu-prefix-cache-scorer
      weight: 1.0
EOF

